import{_ as s,c as a,o as n,a as l}from"./app.741d02cd.js";const i=JSON.parse('{"title":"ohMyZsh配置的笔记","description":"","frontmatter":{"title":"ohMyZsh配置的笔记","tags":["ohMyZsh配置"],"创建时间":"星期五, 八月 5日 2022, 10:32:39 上午","修改时间":"星期日, 十一月 20日 2022, 9:50:35 晚上"},"headers":[{"level":2,"title":"安装","slug":"安装","link":"#安装","children":[]}],"relativePath":"工作区/开发工具/ohMyZsh配置.md"}'),o={name:"工作区/开发工具/ohMyZsh配置.md"},p=l(`<p>#ohMyZsh</p><h1 id="ohmyzsh配置的笔记" tabindex="-1">ohMyZsh配置的笔记 <a class="header-anchor" href="#ohmyzsh配置的笔记" aria-hidden="true">#</a></h1><blockquote><p>作为一个程序员，可以没钱，没车，没房，没老婆，没女朋友。 但是，一定要有一个漂亮骚气的终端。</p></blockquote><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-hidden="true">#</a></h2><ol><li>安装iterm2 或者用系统自带的终端</li></ol><p>切换到zsh，不过现在默认就是zsh</p><ol start="2"><li>使用git clone oh-my-zsh项目</li></ol><p><a href="https://github.com/ohmyzsh/ohmyzsh/blob/master/templates/zshrc.zsh-template" target="_blank" rel="noreferrer">将里面的配置</a>复制到<code>~/.zshrc</code></p><ol start="3"><li>安装p10k</li></ol><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">git clone --depth=1 https://github.com/romkatv/powerlevel10k.git \${ZSH_CUSTOM:-$HOME/.oh-my-zsh/custom}/themes/powerlevel10k</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><ol start="4"><li>配置zsh <code>~/.zshrc</code></li></ol><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">ZSH_THEME=&quot;powerlevel10k/powerlevel10k&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><ol start="5"><li>配置字体</li></ol><p>推荐安装 Nerd Font 字体</p><blockquote><p>google搜索nerd fonts github 下载安装 Hack Nerd Font。</p></blockquote><p>下载好后，配置iterm2或者系统终端的字体</p><ol start="6"><li>配置p10k进行样式配置</li></ol><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># 使用p10k进行整体配置</span></span>
<span class="line"><span style="color:#A6ACCD;">$ p10k configure </span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 使用vscode打开 修改细节</span></span>
<span class="line"><span style="color:#A6ACCD;">$ code </span><span style="color:#89DDFF;">~</span><span style="color:#A6ACCD;">/.p10k.zsh </span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 颜色选择</span></span>
<span class="line"><span style="color:#A6ACCD;">$ </span><span style="color:#89DDFF;">for</span><span style="color:#A6ACCD;"> i </span><span style="color:#89DDFF;">in</span><span style="color:#A6ACCD;"> {0..255}</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">do</span><span style="color:#A6ACCD;"> print -Pn </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">%K{</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">i</span><span style="color:#C3E88D;">}  %k%F{</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">i</span><span style="color:#C3E88D;">}</span><span style="color:#89DDFF;">\${</span><span style="color:#A6ACCD;">(l</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;">3</span><span style="color:#89DDFF;">::</span><span style="color:#A6ACCD;">0</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;">)i</span><span style="color:#89DDFF;">}</span><span style="color:#C3E88D;">%f </span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">\${\${</span><span style="color:#A6ACCD;">(M)$((i</span><span style="color:#89DDFF;">%</span><span style="color:#A6ACCD;">6))</span><span style="color:#89DDFF;">:#</span><span style="color:#A6ACCD;">3</span><span style="color:#89DDFF;">}:</span><span style="color:#A6ACCD;">+</span><span style="color:#89DDFF;">$&#39;</span><span style="color:#A6ACCD;">\\n</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">};</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">done</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 符号</span></span>
<span class="line"><span style="color:#A6ACCD;">$ </span><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f060=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf060</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f061=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf061</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f062=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf062</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f063=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf063</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f0b2=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf0b2</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f06a=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf06a</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f071=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf071</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">f05a=</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\\uf05a</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p><code>p10k.zsh</code> 里面的一些细节配置</p><div class="language-ini"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># 左侧显示部分 </span></span>
<span class="line"><span style="color:#A6ACCD;">POWERLEVEL9K_LEFT_PROMPT_ELEMENTS </span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 右侧显示部分 </span></span>
<span class="line"><span style="color:#A6ACCD;">POWERLEVEL9K_RIGHT_PROMPT_ELEMENTS</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># 指令输入时左边的符号 \`$\` 修改</span></span>
<span class="line"><span style="color:#A6ACCD;">typeset -g POWERLEVEL9K_PROMPT_CHAR_{OK,ERROR}_VIINS_CONTENT_EXPANSION </span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">#版本控制</span></span>
<span class="line"><span style="color:#676E95;">## before</span></span>
<span class="line"><span style="color:#A6ACCD;">    typeset -g </span><span style="color:#F07178;">POWERLEVEL9K_VCS_UNTRACKED_ICON</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">?</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">.....</span></span>
<span class="line"><span style="color:#A6ACCD;">    (( VCS_STATUS_NUM_CONFLICTED )) &amp;&amp; res+=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> \${conflicted}~\${VCS_STATUS_NUM_CONFLICTED}</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    (( VCS_STATUS_NUM_STAGED     )) &amp;&amp; res+=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> \${modified}+\${VCS_STATUS_NUM_STAGED}</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    (( VCS_STATUS_NUM_UNSTAGED   )) &amp;&amp; res+=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> \${modified}!\${VCS_STATUS_NUM_UNSTAGED}</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">## after</span></span>
<span class="line"><span style="color:#A6ACCD;">    typeset -g </span><span style="color:#F07178;">POWERLEVEL9K_VCS_UNTRACKED_ICON</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">\\u</span><span style="color:#C3E88D;">F059</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">.....</span></span>
<span class="line"><span style="color:#A6ACCD;">    (( VCS_STATUS_NUM_CONFLICTED )) &amp;&amp; res+=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> \${conflicted}</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">$</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">\\u</span><span style="color:#C3E88D;">F28D</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\${VCS_STATUS_NUM_CONFLICTED}</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    (( VCS_STATUS_NUM_STAGED     )) &amp;&amp; res+=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> \${modified}</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">$</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">\\u</span><span style="color:#C3E88D;">F055</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\${VCS_STATUS_NUM_STAGED}</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    (( VCS_STATUS_NUM_UNSTAGED   )) &amp;&amp; res+=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> \${modified}</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">$</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">\\u</span><span style="color:#C3E88D;">F06A</span><span style="color:#89DDFF;">&#39;&quot;</span><span style="color:#C3E88D;">\${VCS_STATUS_NUM_UNSTAGED}</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p><a href="https://blog.51cto.com/u_14415843/2494693" target="_blank" rel="noreferrer">参考链接1</a><a href="https://www.onejar99.com/zsh-powerlevel10k-custom-config-note/" target="_blank" rel="noreferrer">配置p10k</a></p>`,21),e=[p];function t(c,r,D,y,C,F){return n(),a("div",null,e)}const _=s(o,[["render",t]]);export{i as __pageData,_ as default};
