import{_ as e,c as a,o as t,d as i}from"./app.0b246d93.js";const f=JSON.parse('{"title":"读书记录的笔记","description":"","frontmatter":{"title":"读书记录的笔记","tags":["读书记录"],"创建时间":"星期日, 十一月 20日 2022, 3:03:24 下午","修改时间":"星期日, 十一月 20日 2022, 3:25:59 下午"},"headers":[{"level":2,"title":"打开心智","slug":"打开心智","link":"#打开心智","children":[{"level":3,"title":"多巴胺是快感还是动机呢？","slug":"多巴胺是快感还是动机呢","link":"#多巴胺是快感还是动机呢","children":[]}]}],"relativePath":"工作区/感悟/读书记录.md"}'),l={name:"工作区/感悟/读书记录.md"},r=i('<p>#记录</p><h1 id="读书记录的笔记" tabindex="-1">读书记录的笔记 <a class="header-anchor" href="#读书记录的笔记" aria-hidden="true">#</a></h1><h2 id="打开心智" tabindex="-1">打开心智 <a class="header-anchor" href="#打开心智" aria-hidden="true">#</a></h2><h3 id="多巴胺是快感还是动机呢" tabindex="-1">多巴胺是快感还是动机呢？ <a class="header-anchor" href="#多巴胺是快感还是动机呢" aria-hidden="true">#</a></h3><p>传统观点认为多巴胺调控了快感和动机</p><p>肯特贝里奇 和 特里罗宾逊 1990年研究发现 当降低多巴胺浓度时， 小鼠的愉悦感没有变化，但是动机明细变弱了。</p><p>进一步研究，发现我们的快感是由大脑中一系列的“享乐热点”调控的。</p><p>这些享乐热点非常小，并且藏在好几个不同的脑区内部。他们由另一套复杂的机制调节。跟多巴胺一点关系都没有。</p><ol><li>多巴胺调控的是动机而不是快乐</li><li>当我们获得正向反馈是，大脑就会分泌多巴胺，从而激活奖赏回路，激发我们的动机。</li><li>在奖赏回路中，多巴胺的浓度越高，我们的动机也就越强。</li><li>快乐是由享乐热点调控，受另一套机制影响，跟多巴胺无关。</li></ol><blockquote></blockquote>',10),n=[r];function o(_,d,s,c,h,p){return t(),a("div",null,n)}const m=e(l,[["render",o]]);export{f as __pageData,m as default};
